import React from "react";
import { View, Text, Button, StyleSheet } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import CameraScreen from "./camera";
import logo from '../assets/logo.png';
const Tab = createBottomTabNavigator();

const HomeLayout = ({ navigation }: any) => {
  return (
    <View style={styles.container}>
      <Text style={{ padding: 10 }}>
        <h3><img src={logo} style={{ width: 50, height: 50 }}></img>: 0.00 REN</h3>
        <ol>
          <li>Take a picture of your messy room.</li>
          <li>Take a picture of your clean room.</li>
          <li>Earn REN coin!</li>
        </ol>
      </Text>
      <Text style={{ padding: 10 }}>Take Photo of a clean room</Text>
      <Button
        title="Click Photo"
        onPress={() => navigation.navigate("Camera")}
      ></Button>
    </View>
  );
};

const HomeScreen = () => {
  return (
    <NavigationContainer independent={true}>
      <Tab.Navigator>
        <Tab.Screen
          name="Home"
          component={HomeLayout}
          options={{ tabBarVisible: false }}
        ></Tab.Screen>
        <Tab.Screen
          name="Camera"
          component={CameraScreen}
          options={{ tabBarVisible: false }}
        ></Tab.Screen>
      </Tab.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 1,
  },
});

export default HomeScreen;
